// Main Data Query Whenever you type
var _o = new Object();
function dataQuery( obj ){
	_o.x = obj.id;
	var query = _o[_o.x]["query"];
	//if no data, no need to show menu
	if( !query ) {
		if( $( "burmese" ) && !_o[_o.x]["self"] ) $("burmese").value = "";
		_hide( _o.x  + "drop");
		return ;
	}
	
	//get menu from dictionary
	var data=_getMenuData( obj, query); var dlen = data.length;
	//get Numbers
	data = _getNumbers (obj, query, data);
    
    data = _getMenuEx(obj, query, data);
    data = _getMenuExUpd(obj, query, data);
	
	if(!_o[_o.x]["_limit"]){
		_o[_o.x]["noitem"]=data.length==0?(_o[_o.x]["noitem"]+1):0;
		//self.status = _o[_o.x]["noitem"];
		if(_o[_o.x]["noitem"]==0){_o[_o.x]["hist"]=[];_o[_o.x]["hist"][0]=data[0];}
		if(_o[_o.x]["noitem"]==(_o[_o.x]["_selectFirst"]?1:2) && query.length>_o[_o.x]["noitem"]){ //if can't find in 2 char, force update it.
			var tmp=_o[_o.x]["query"];
			_o[_o.x]["query"]=_o[_o.x]["hist"][0][0];
			_o[_o.x]["list"]=[_o[_o.x]["hist"][0]];_o[_o.x]["count"]=1;
			_onMDown_(obj);
			_o[_o.x]["query"]=query.substring(query.length-_o[_o.x]["noitem"], query.length);
			_o[_o.x]["noitem"]=0;
		}
	}
	
	_showMenu( obj, query, data );
};

//Main Generator for Burglish Menu Datas
function _getMenuEx(obj, query, data){
	var _r0;var _r1;var _r2; var _cons1;var _cons2;var _cons3;var _cons4;
	var help  = "" + query + "";
	_o[_o.x]["lower"]=(query.charCodeAt(0)>64 && query.charCodeAt(0)<91) ?true:false;
	//consonent main loop
	var _bcCheck=false;
	for(var k=0; k<_b.c.length; k++){
		/*only do when its match first char*/
		var qLower=query.toLowerCase();
		if(qLower[0]!=_b.c[k][0][0]) {if(_bcCheck) break;continue;}_bcCheck=true;
		//case insensitive consonent
		var idx=_b.c[k][0];
		if(qLower==idx) query = query + "a";
		_r0 = eval( "/^" + idx  +"/i");
		//vowel only
		//test for particular consonent
		if ( _r0.test( query )){
			//consonent particles loop
			for( var i1=0; i1 < _b.c[k][1].length; i1++){
				_cons1 = _b.c[k][1][i1];
				
				//if patsints
				if(_o[_o.x]["lower"]){
					if(bord(_o[_o.x]["plChar"])!=4){//not nga that
						_cons1 = bLower(_b.c[k][1][i1],_o[_o.x]["burglish_font"]);
					}
				}
				
				//put consonent or patsint consonent
				if(/^[aeiou]/i.test(query)) {
					query = query.replace(/^([aeiou])/i,"$1$1");
				}
				_cons2=query.replace(_r0,_cons1);

				//vowels main loop
				var _bvCheck=false;
				for(var i = 0; i < _b.v.length ; i++ ) {
					/*only do when its match first char*/
					if(query[idx.length]!=_b.v[i][0][0])  {if(_bvCheck) break;continue;};_bvCheck=true;
					_r1 = eval( "/" + "(.*)" +  _b.v[i][0] +"$/");
					
					for(var n=0; n<_b.v[i][1].length; n++){
						_cons3 = _cons2.replace( _r1   , _b.v[i][1][n] );
						
						//auto correct
						if( !/\w/.test(_cons3) ){
							for(var l=0; l<_b.a.length; l++){
								 _r2 = eval( "/" + _b.a[l].m +"/");
								 if( _r2.test ( _cons3 ) ){
									 for( var y in _b.a[l].r ){
										_r2 = eval( "/" + y  +"/");
										_cons3 = _cons3.replace( _r2  , _b.a[l].r[y] );
									 }
								 }
							}

							var exist = false;
							for( var p=0; p<data.length;p++){
								if(data[p][1]=== _cons3) { exist = true; break; }
							} 
							if(!exist){
								var burglish_filter=false;
								for(var i3=0;i3<_b.f.length;i3++){
									if(eval("/"+_b.f[i3]+"/").test(_cons3)) {
										burglish_filter=true;break;
									}
								}
								if(!burglish_filter && _o[_o.x]["_limit"]){
									if(burglish_syllable[_dfont] && _cons3.length>1){
										if(burglish_syllable[_dfont].indexOf(","+_cons3+",")==-1) burglish_filter=true;
									}
								}
								if(!burglish_filter){
									data[data.length] = [ help, (_o[_o.x]["lower"]?"---":"") + _cons3] ;
									_o[_o.x]["list"][data.length-1] = eval(data[data.length-1]);
								}
							}
							if(data.length>20) return data;
						}//auto correct test
					}// end vowels particles loop
				}//vowels main loop
			}//consonent particles loop
		}//test for particular consonent
	}//consonent main loop
	return data;
}

//Burglish Menu Extra Items
function _getMenuExUpd(obj, query, data){
    //special characters
    for(var i2=0;i2<data.length;i2++){
        for(var i3=0;i3<_b.p.length;i3++){
            var re2 = eval("/" + _b.p[i3][0] + "/");
            if( re2.test(data[i2][1]) ){
                var _cons3 = data[i2][1].replace(re2,_b.p[i3][1]);
                var exist = false;
				for( var i4=0; i4<data.length;i4++){
					if(data[i4][1]== _cons3) { exist = true; break; }
				} 
				if(!exist){
					var burglish_filter=false;
					for(var i4=0;i4<_b.f.length;i4++){
						if(eval("/"+_b.f[i4]+"/").test(_cons3)) {
							burglish_filter=true;break;
						}
					}
					if(!burglish_filter){
						data[data.length] = [ data[i2][0], _cons3 ] ;
					}
                }
            }
        }
    }
    // rearrange
	var datatmp=[];
	for(var i2=0;i2<data.length;i2++){
		if(data[i2].length>=3){
            if(data[i2][2]!=-1){
                datatmp[datatmp.length] = data[i2];
			}
        }else{
			datatmp[datatmp.length] = data[i2];
		}
    }
	for(var i2=0;i2<data.length;i2++){
		if(data[i2].length>=3){
            if(data[i2][2]==-1){
                datatmp[datatmp.length] = data[i2];
			}
        }
    }
	data=datatmp;
	
	//convert burglish_font in burglish menu
	if(_o[_o.x]["burglish_font"]!="Zawgyi_One"){
		var tmp="";
		for(var i2=0;i2<data.length;i2++){
			tmp+= (i2>0?'||':'')+data[i2][1];
		}
		tmp=tmp.convertFont("Zawgyi_One",_o[_o.x]["burglish_font"], {"noRepeat":true}).split('||');
		for(var i2=0;i2<data.length;i2++){
			data[i2][1]=tmp[i2];
		}
	}
	
	_o[_o.x]["list"] = data;
    return data;
}

